// Parse binary data from IKEAAirMonitor
const buf = msg.payload;
const timestamp = Date.now(); // Milliseconds for InfluxDB v2
const location = "Büro";
const device_id = ".223";
const device_type = "IKEAAirMonitor";

// Parse the 14-byte binary payload
const pm25 = buf.readUInt16LE(0);      // PM2.5 in µg/m³
const temperature = buf.readFloatLE(2); // Temperature in °C
const humidity = buf.readFloatLE(6);    // Humidity in %
const pressure = buf.readFloatLE(10);   // Pressure in hPa

function getNumericValue(value, defaultValue = 0) {
    return (value !== null && value !== undefined && !isNaN(value)) ? Number(value) : defaultValue;
}

function getBoolAsInt(value) {
    return value ? 1 : 0;
}

// Calculate simple AQI based on PM2.5 (US EPA standard)
function calculatePM25AQI(pm25) {
    if (pm25 <= 12.0) return Math.round(pm25 * 50 / 12.0);
    if (pm25 <= 35.4) return Math.round(51 + (pm25 - 12.1) * 49 / 23.3);
    if (pm25 <= 55.4) return Math.round(101 + (pm25 - 35.5) * 49 / 19.9);
    if (pm25 <= 150.4) return Math.round(151 + (pm25 - 55.5) * 49 / 94.9);
    if (pm25 <= 250.4) return Math.round(201 + (pm25 - 150.5) * 99 / 99.9);
    return Math.round(301 + (pm25 - 250.5) * 99 / 249.9);
}

// Calculate dew point using Magnus formula
function calculateDewPoint(temp, humidity) {
    const a = 17.27;
    const b = 237.7;
    const alpha = ((a * temp) / (b + temp)) + Math.log(humidity / 100.0);
    return (b * alpha) / (a - alpha);
}

// Calculate comfort index based on temperature and humidity
function calculateComfortIndex(temp, humidity) {
    // Simple comfort assessment: ideal is 20-24°C and 40-60% humidity
    let tempScore = 100;
    let humidityScore = 100;

    if (temp < 18 || temp > 26) tempScore = Math.max(0, 100 - Math.abs(temp - 22) * 10);
    if (humidity < 30 || humidity > 70) humidityScore = Math.max(0, 100 - Math.abs(humidity - 50) * 2);

    return (tempScore + humidityScore) / 2;
}

const thresholds = {
    aqi_index: 100,
    pm2_5_ugm3: 35,
    humidity_low: 30,
    humidity_high: 70,
    temperature_low: 18,
    temperature_high: 26
};

const aqi = calculatePM25AQI(pm25);
const dewPoint = calculateDewPoint(temperature, humidity);
const comfortIndex = calculateComfortIndex(temperature, humidity);

const influxObject = {
    measurement: "air_quality",
    tags: {
        device_id: device_id,
        location: location,
        device_type: device_type,
        data_type: "environmental"
    },
    fields: {
        // Environmental data
        temperature_celsius: getNumericValue(temperature),
        humidity_percent: getNumericValue(humidity),
        pressure_hpa: getNumericValue(pressure),
        dew_point_celsius: getNumericValue(dewPoint),

        // Air quality data
        pm2_5_ugm3: getNumericValue(pm25),
        aqi_index: getNumericValue(aqi),
        aqi_category: aqi <= 50 ? 1 : aqi <= 100 ? 2 : aqi <= 150 ? 3 : aqi <= 200 ? 4 : 5,
        pm2_5_aqi: getNumericValue(aqi), // Same as overall AQI since we only have PM2.5

        // Comfort metrics
        comfort_index: getNumericValue(comfortIndex),

        // System metrics
        sensor_reliable: 1, // Assume reliable if we got data
        timestamp: timestamp
    }
};

// Calculate alerts
const alerts = {
    alert_aqi: aqi > thresholds.aqi_index,
    alert_pm25: pm25 > thresholds.pm2_5_ugm3,
    alert_humidity_low: humidity < thresholds.humidity_low,
    alert_humidity_high: humidity > thresholds.humidity_high,
    alert_temperature_low: temperature < thresholds.temperature_low,
    alert_temperature_high: temperature > thresholds.temperature_high
};

// Add alert fields to influx object
Object.assign(influxObject.fields, Object.fromEntries(
    Object.entries(alerts).map(([k, v]) => [k, getBoolAsInt(v)])
));

// Create readable data object for debugging/other nodes
const readableData = {
    temperature: temperature,
    humidity: humidity,
    pressure: pressure,
    pm25: pm25,
    aqi: aqi,
    dewPoint: dewPoint,
    comfortIndex: comfortIndex,
    alerts: alerts,
    timestamp: new Date(timestamp).toISOString()
};

msg.payload = [influxObject];
msg.readable_data = readableData; // Keep readable data for other nodes

node.log(`IKEAAirMonitor data - PM2.5: ${pm25}µg/m³, AQI: ${aqi}, T: ${temperature.toFixed(1)}°C, H: ${humidity.toFixed(1)}%, P: ${pressure.toFixed(1)}hPa`);

// Only log alerts if any exist
const activeAlerts = Object.entries(alerts).filter(([k, v]) => v).map(([k, v]) => k);
if (activeAlerts.length > 0) {
    node.log(`ALERTS: ${activeAlerts.join(', ')}`);
}

return msg;
